import cards_back from '../../assets/cards_back.png';
import './Card.css';

export interface CardProps {
    id: number,
    src: string,
    flipped: boolean,
    matched: boolean,
    disabled: boolean,
    onClick: () => void,
}

const Card = (props: CardProps) => {
    return (
      <button className={`card ${props.flipped? "card-flipped" : ""}`} onClick={props.onClick} disabled={props.disabled}>
        {/* <button className='card' onClick={props.onClick} disabled={props.disabled}> */}
        <div className="card-inner">
          <img className="card-back" src={cards_back} alt="Card Back" />
          <img className="card-front" src={props.src} alt={`card-${props.id}`} />
          </div>
            {/* {props.flipped ? (
            <img src={props.src} alt={`card-${props.id}`} />
          ) : (
            <img src={cards_back} alt="Card Back" />
          )} */}
        </button>
    )
}

export default Card;
