import cards_back from '../../assets/cards_back.png';
import './Card.css';

export interface CardProps {
    id: number,
    src: string,
    flipped: boolean,
    matched: boolean,
    disabled: boolean,
    onClick: () => void,
}

const Card = (props: CardProps) => {
    return (
      <button className={`card ${props.flipped? "flipped" : ""}`} onClick={props.onClick} disabled={props.disabled}>
        {/* <button className='card' onClick={props.onClick} disabled={props.disabled}> */}

            {props.flipped ? (
            <img src={props.src} alt={`card-${props.id}`} />
          ) : (
            <img src={cards_back} alt="Card Back" />
          )}
        </button>
    )
}

export default Card;
