import Card from '../Card/Card'
import { schuffledCards } from '../../utils/schuffledCards.util';
import { useEffect, useState } from 'react';
import { CardProps } from '../Card/Card';
import './Cards.css';


const Cards = () => {
    const [cardProps, setCardProps] = useState<CardProps[]>([])
    const [clickedCardSrc, setClickedCardSrc] = useState<string>('')
    useEffect(() => {
        setCardProps(schuffledCards())
        console.log(cardProps)
    }, [])

    const handleClick = (id: number) => {

        setCardProps((prevState) => {
            let newCardProps = prevState.map((card) =>
                card.id === id ? { ...card, flipped: !card.flipped } : card
            )

            const clickedCard = newCardProps.find((card) => card.id === id)

            if (clickedCardSrc === '') {
                setClickedCardSrc(clickedCard!.src)
            }

            else if
                (clickedCard!.src === clickedCardSrc) {
                newCardProps = newCardProps.map((card) =>
                    card.src === clickedCardSrc ? { ...card, flipped: true, matched: true } : card)
                setClickedCardSrc('')
            }

            else {
                setClickedCardSrc('')
                setTimeout(() => {
                    setCardProps((prevState) =>
                        prevState.map((card) =>
                            !card.matched ? { ...card, flipped: false } : card
                        ))
                }, 1000)
            }
            return newCardProps;
        })
    }

    return (
        <div className="cards">
            {cardProps.map((props) => (
                <Card
                    key={props.id}
                    id={props.id}
                    src={props.src}
                    flipped={props.flipped}
                    matched={props.matched}
                    disabled={props.disabled}
                    onClick={() => handleClick(props.id)}>
                </Card>
            )
            )}
        </div>
    )
}

export default Cards;
