import Card from '../Card/Card'
import { schuffledCards } from '../../utils/schuffledCards.util';
import { useEffect, useState } from 'react';
import { CardProps } from '../Card/Card';
import './Cards.css';


const Cards = () => {
    const [cardProps, setCardProps] = useState<CardProps[]>([])
    const [clickedCardSrc, setClickedCardSrc] = useState<string>('')
    useEffect(() => {
        setCardProps(schuffledCards())
        console.log(cardProps)
    }, [])

    const handleClick = (id: number) => {
        setCardProps((prevState) =>  {
            const newCardProps = prevState.map((card) =>
                card.id === id ? { ...card, flipped: !card.flipped } : card
            )
    })

        const clickedCard = newCardProps.find((card) => card.id === id)

        if (clickedCardSrc === ''){
            setClickedCardSrc(clickedCard!.src)  
        } 

        else if
        (clickedCard!.src  === clickedCardSrc){
            setCardProps((prevState) =>  prevState.map((card) =>
                    card.src === clickedCardSrc ? { ...card, matched: true, disabled: true} : card
                )
            )
            setClickedCardSrc('')
        } else {
            setTimeout(() => { 
            setCardProps((prevState) => 
                 prevState.map((card) =>
                    card.flipped ? { ...card, flipped: false} : card
                ))
                setClickedCardSrc('')
             }, 1000)
        }
       return newCardProps 
    }

    return (
        <div className="cards">
            {cardProps.map((props) => (
                <Card key={props.id} id={props.id} src={props.src} flipped={props.flipped} matched={props.matched} onClick={() => handleClick(props.id)}></Card>
            )
            )}
        </div>
    )
}

export default Cards;
