import Card from '../Card/Card'
import { buildCardProps } from '../../utils/card.util';
import { useEffect, useState } from 'react';
import { CardProps } from '../Card/Card';


const Cards = () => {
    const [cardProps, setCardProps] = useState<CardProps[]>([])
    useEffect(() => {
        setCardProps(buildCardProps())
    }, [])

    return (
        <>
            {cardProps.map((props) => (
                <Card id = {props.key} src={props.src} flipped={props.flipped}></Card>
            )
            )}
        </>
)
}

export default Cards;
